import { useState } from "react";
import Button from './Button'
import Total from './Total'



const App = () => {
  const [counter, setCounters] = useState({
	bad: 0,
	neutral: 0,
	good: 0
})

const handleBad = () => {
	const newFeedback = {
		bad: counter.bad + 1,
		neutral: counter.neutral,
		good: counter.good
	}
	setCounters(newFeedback)
}

const handleNeutral = () => {
	const newFeedback = {
		bad: counter.bad,
		neutral: counter.neutral + 1,
		good: counter.good
	}
	setCounters(newFeedback)
}

const handleGood = () => {
	const newFeedback = {
		bad: counter.bad,
		neutral: counter.neutral,
		good: counter.good + 1
	}
	setCounters(newFeedback)
}


  return (
    <div>
    <h1>give feedback</h1>
    <Button onClick = {handleGood} name = "good"/>
    <Button onClick = {handleNeutral} name = "neutral"/>
    <Button onClick = {handleBad} name = "bad"/>
    <h1>statistics</h1>
    <Total />

    </div>
  );
}

export default App;
